<?php/** * The template for displaying Archive pages. * * @package DeepBlue */get_header(); ?>	<?php if ( have_posts() ) : ?>    <?php $post = $posts[0]; ?>    <?php if (is_category()) { ?>    <h1 class="result_title">Archive for the :<?php single_cat_title(); ?></h1>    <?php } elseif( is_tag() ) { ?>    <h1 class="result_title">Posts Tagged :<?php single_tag_title(); ?></h1>    <?php } elseif (is_day()) { ?>    <h1 class="result_title">Archive for :<?php the_time('Y/m/d'); ?></h1>    <?php } elseif (is_month()) { ?>    <h1 class="result_title">Archive for :<?php the_time('Y/m'); ?></h1>    <?php } elseif (is_year()) { ?>    <h1 class="result_title">Archive for :<?php the_time('Y'); ?></h1>    <?php  } elseif (is_author()) { ?>    <h1 class="result_title">Author Archive</h1>    <?php } ?>    <?php while ( have_posts() ) : the_post(); ?>		<?php get_template_part('content',get_post_format());?>	<?php endwhile; ?>	<?php else : ?>		<?php get_template_part( 'content', 'none' ); ?>	<?php endif; ?><?php get_footer(); ?>