<?php/** * The template for displaying Comments. * * The area of the page that contains comments and the comment form. * * @package DeepBlue */if ( post_password_required())	return;/*==================================================CommentPrompt==================================================*/$commenter = wp_get_current_commenter();$req = get_option( 'require_name_email' );$aria_req = ( $req ? " aria-required='true'" : '' );//InputArea$fields = array(	'author' => '<section class="left_content">' . '<label for="author">' . __( 'Name' )                . ( $req ? '*' : '' ) . '</label> ' .                '<input id="author" name="author" type="text" value="'                . esc_attr( $commenter['comment_author'] ) . '"ã€€' . $aria_req . ' />',    'email'  => '<label for="email">' . __( 'Email' )                . ( $req ? '*/Not published' : '' ) . '</label> ' .                '<input id="email" name="email" type="text" value="'                . esc_attr(  $commenter['comment_author_email'] ) . '" size="30"' . $aria_req . ' />',    'url'    => '<label for="website">' . __( 'Website' ) . '</label>' .	                '<input name="url" type="url" value="'	                . esc_attr( $commenter['comment_author_url'] ) . '" /></section>',    );//CommentArea$comment_field = '<section class="right_content"><label for="comment">'. _x( 'Comment', 'noun' ) . '*</label><textarea id="comment" name="comment"  aria-required="true"></textarea></section>';//options$comment_notes_before = NULL;$title_reply = NULL;$args = array(    'fields'        => apply_filters( 'comment_form_default_fields', $fields ),    'comment_field'     => $comment_field,    'comment_notes_before'  => $comment_notes_before,    'title_reply'=> $title_reply,);?><div id="comments"><h1>Comments</h1><section id="input_area" class="clearfix"><?php comment_form($args); ?></section><!-- /#input_area --><?php if ( post_password_required())return; ?><section id="view_area">	<ul class="commentlist">		<?php wp_list_comments('type=comment&callback=mytheme_comment'); ?>	</ul><?php if ( ! comments_open() && get_comments_number() ) : ?>	<p class="no-comments"><?php _e( 'Comments are closed.' , '' ); ?></p><?php endif; ?></section><!-- /#view_area --></div><!-- /#comments -->