<?php/** * The template for displaying all pages. * * This is the template that displays all pages by default. * Please note that this is the WordPress construct of pages * and that other 'pages' on your WordPress site will use a * different template. * * @package DeepBlue */get_header();?>	<?php while ( have_posts() ) : the_post(); ?>	<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>		<h1><?php the_title();?></h1>		<section class="content clearfix">			<?php the_post_thumbnail('page');?>			<?php the_content();?>		</section><!-- /.content clearfix-->	</article>	<?php endwhile;?>	<?php comments_template();?><?php get_footer();?>