<?php/** * The template for displaying Search Results pages. * * @package DeepBlue */get_header(); ?>	<?php if ( have_posts() ) : ?>		<h1 class="result_title">Search Results for:<span><?php the_search_query(); ?></span></h1>		<?php			global $query_string;			query_posts($query_string . "&post_type=post");		?>		<?php while ( have_posts() ) : the_post(); ?>			<?php get_template_part( 'content', get_post_format() ); ?>		<?php endwhile; ?>		<?php else : ?>			<?php get_template_part( 'content', 'none' ); ?>		<?php endif; ?><?php get_footer(); ?>