<?php/** * The template for displaying Tag pages. * * * @package DeepBlue */ get_header(); ?>	<?php if ( have_posts() ) : ?>		<h1 class="result_title">Tag Archives:<span><?php  single_cat_title(); ?></span></h1>		<?php while ( have_posts() ) : the_post(); ?>			<?php get_template_part( 'content', get_post_format() ); ?>		<?php endwhile; ?>		<?php else : ?>			<?php get_template_part( 'content', 'none' ); ?>		<?php endif; ?><?php get_footer(); ?>