<?php/** * The template for displaying Author pages. * * * @package DeepBlue */get_header(); ?>	<?php if ( have_posts() ) : the_post();?>	<header class="author_header">		<h1><?php get_the_author('aim');?></h1>		<?php if ( get_the_author_meta( 'description' ) ) : ?>		<p><?php the_author_meta( 'description' ); ?></p>		<?php endif; ?>	</header><!-- /.author_header -->	<?php rewind_posts();?>	<?php while ( have_posts() ) : the_post();?>		<?php get_template_part( 'content', get_post_format() );?>	<?php endwhile;?>	<?php else : ?>		<?php get_template_part( 'content', 'none' ); ?>	<?php endif; ?><?php get_footer(); ?>